const showData=a=>{console.log(a);var t=a.length;for(i=0;i<t;i++)document.querySelector(".product__list").innerHTML+=`
        <li class="product__item">
            <img class="product__image" src="${a[i].api_featured_image}" alt="${a[i].name}">
            <div class="product__information">
                <h1 class="product__name">${a[i].name}</h1>
                <span class="product__price-brand">
                    <p class="product__brand">${a[i].brand}</p>
                    <p class="product__price">$${a[i].price}</p>
                </span>
            </div>

            <ul class="product__data">
                <li class="product__data-item">Brand <p>${a[i].brand}</p></li>
                <li class="product__data-item">Price <p>$${a[i].price}</p></li>
                <li class="product__data-item">Rating <p>${a[i].rating}</p></li>
                <li class="product__data-item">Category <p>${a[i].category}</p></li>
                <li class="product__data-item">Product Type <p>${a[i].product_type}</p></li>
            </ul>
        </li>
        `};window.onload=a=>{fetch("http://makeup-api.herokuapp.com/api/v1/products.json?rating_greater_than=4.9",{method:"GET",mode:"cors",cache:"default"}).then(a=>{a.json().then(a=>showData(a))}).catch(a=>console.log("Erro: "+a,message))};